INSERT INTO news (nid, chid, title, link, description, "pubDate", likes) VALUES (91, 6, 'В России на 300 рублей вырос минимальный размер оплаты труда', 'https://news.yandex.ru/yandsearch?cl4url=ria.ru%2Feconomy%2F20170701%2F1497641852.html', 'С первого июля минимальный размер оплаты труда (МРОТ) повысился на 300 рублей. Теперь МРОТ составит 7,8 тысяч рублей в месяц. Отныне работодатель не может установить ежемесячную заработную плату ниже этой суммы, если сотрудник отработал норму.', '2017-07-01 04:05:59+04', 0);
