INSERT INTO news (nid, chid, title, link, description, "pubDate", likes) VALUES (94, 6, 'Около 450 рейсов задержали в аэропортах Москвы из-за непогоды', 'https://news.yandex.ru/yandsearch?cl4url=iz.ru%2Fnode%2F613339', 'В аэропортах Москвы задержано уже 450 авиарейсов и более 40 отменены из-за ливня, обрушившегося 30 июня на столичный регион.', '2017-07-01 08:07:39+04', 0);
INSERT INTO news (nid, chid, title, link, description, "pubDate", likes) VALUES (95, 6, 'Лавров и Киссинджер обсудили «ненормальные» отношения России и США', 'https://news.yandex.ru/yandsearch?cl4url=www.rbc.ru%2Fpolitics%2F30%2F06%2F2017%2F59560ec59a794776dc10e7a2', 'Министр иностранных дел России Сергей Лавров на «Примаковских чтениях» совместно с бывшим госсекретарем США Генри Киссинджером обсудили внешнюю политику обеих стран. Глава МИД при этом затронул тему будущей встречи президентов Владимира Путина и Дональда Трампа, сообщает корреспондент РБК.', '2017-06-30 12:52:20+04', 0);
INSERT INTO news (nid, chid, title, link, description, "pubDate", likes) VALUES (96, 6, 'Путин и Лукашенко заглянули в будущее Союзного государства', 'https://news.yandex.ru/yandsearch?cl4url=www.gazeta.ru%2Fbusiness%2F2017%2F06%2F30%2F10756133.shtml', 'Белоруссия будет мировой IT-державой, судьба России решится в битве за роботизацию экономики, а между странами отменят роуминг. Таким увидели будущее Союзного государства президенты Владимир Путин и Александр Лукашенко.', '2017-06-30 23:00:50+04', 0);
INSERT INTO news (nid, chid, title, link, description, "pubDate", likes) VALUES (97, 6, 'Один человек погиб в результате стрельбы в Нью-Йорке', 'https://news.yandex.ru/yandsearch?cl4url=ria.ru%2Fworld%2F20170701%2F1497642543.html', 'Один человек погиб, еще несколько ранены при стрельбе в нью-йоркской больнице, нападавший покончил с собой. Об этом сообщил мэр города Билл де Блазио. Как заявил градоначальник, погибший был врачом.', '2017-07-01 02:05:00+04', 0);
INSERT INTO news (nid, chid, title, link, description, "pubDate", likes) VALUES (98, 6, 'Пятничный дождь принес в Москву более 90% месячной нормы осадков', 'https://news.yandex.ru/yandsearch?cl4url=ria.ru%2Fsociety%2F20170701%2F1497648128.html', 'Более 90% от месячной нормы осадков принес в Москву ливень, начавшийся в последний июньский день, сообщили РИА Новости в погодном центре &quot;Фобос&quot;.', '2017-07-01 10:39:49+04', 0);
INSERT INTO news (nid, chid, title, link, description, "pubDate", likes) VALUES (99, 6, 'Россия вошла в пятерку стран с крупнейшей теневой экономикой', 'https://news.yandex.ru/yandsearch?cl4url=www.rbc.ru%2Feconomics%2F30%2F06%2F2017%2F595649079a79470e968e7bff', 'Россия вошла в пятерку крупнейших теневых экономик, заняв четвертое место в рейтинге, куда включены 28 стран.', '2017-06-30 18:28:10+04', 0);
